mapper.update:
update course
course_code = #{courseCode}, course_name = #{courseName}, course_type = #{courseType}, credits = #{credits}, hours = #{hours}, major_id = #{majorId}, semester_order = #{semesterOrder}, description = #{description}, updated_time = now()
where course_id = #{courseId}

mapper.select:
select count(1)
from course c
left join major m on c.major_id = m.major_id


        and (c.course_name like concat('%', #{name}, '%') or c.course_code like concat('%', #{name}, '%'))


        and c.course_type = #{courseType}


        and c.major_id = #{majorId}

select c.course_id, c.course_code, c.course_name, c.course_type, c.credits,
       c.hours, m.major_name, c.semester_order
from course c
left join major m on c.major_id = m.major_id


        and (c.course_name like concat('%', #{name}, '%') or c.course_code like concat('%', #{name}, '%'))


        and c.course_type = #{courseType}


        and c.major_id = #{majorId}


order by c.course_code
limit #{limit} offset #{offset}
