mapper.update:
update social_practice
practice_name = #{practiceName}, practice_type = #{practiceType}, organizer = #{organizer}, start_date = #{startDate}, end_date = #{endDate}, description = #{description},
where practice_id = #{practiceId}

update student_practice
role = #{role}, duration = #{duration}, performance_score = #{performanceScore}, evaluation = #{evaluation}, certificate_url = #{certificateUrl}, status = #{status},
where record_id = #{recordId}

mapper.select:
select count(*) from student_practice sp left join social_practice p on sp.practice_id = p.practice_id left join student s on sp.student_id = s.student_id
and (s.name like concat('%', #{keyword}, '%') or p.practice_name like concat('%', #{keyword}, '%'))
and p.practice_type = #{practiceType}
and sp.status = #{status}

select sp.record_id, sp.student_id, sp.practice_id, sp.role, sp.duration, sp.performance_score, sp.evaluation, sp.certificate_url, sp.status, sp.created_time, sp.updated_time, p.practice_name, p.practice_type, p.organizer, p.start_date, p.end_date, p.description, s.student_no, s.name as student_name from student_practice sp left join social_practice p on sp.practice_id = p.practice_id left join student s on sp.student_id = s.student_id
and (s.name like concat('%', #{keyword}, '%') or p.practice_name like concat('%', #{keyword}, '%'))
and p.practice_type = #{practiceType}
and sp.status = #{status}
order by sp.created_time desc limit #{offset}, #{limit}
