mapper.select:
select practice_id, practice_name, practice_type, organizer, start_date, end_date, description, created_time, updated_time
from social_practice 
where practice_id = #{practiceId}

mapper.update:
update social_practice
practice_name = #{practiceName}, practice_type = #{practiceType}, organizer = #{organizer}, start_date = #{startDate}, end_date = #{endDate}, description = #{description}, updated_time = now()
where practice_id = #{practiceId}

