mapper.update:
update student_practice
student_id = #{studentId}, practice_id = #{practiceId}, role = #{role}, duration = #{duration}, performance_score = #{performanceScore}, evaluation = #{evaluation}, certificate_url = #{certificateUrl}, status = #{status}, reviewer_id = #{reviewerId}, review_time = #{reviewTime}, review_comment = #{reviewComment}, updated_time = now()
where record_id = #{recordId}

mapper.select:
select count(1) from student_practice sp left join student s on sp.student_id = s.student_id left join social_practice p on sp.practice_id = p.practice_id
and (s.name like concat('%', #{name}, '%') or s.student_no like concat('%', #{name}, '%') or p.practice_name like concat('%', #{name}, '%'))
and p.practice_type = #{practiceType}
and sp.status = #{status}

select sp.record_id, sp.student_id, s.student_no, s.name as student_name, sp.practice_id, p.practice_name, p.practice_type, p.organizer, sp.role, sp.duration, sp.performance_score, sp.status from student_practice sp left join student s on sp.student_id = s.student_id left join social_practice p on sp.practice_id = p.practice_id
and (s.name like concat('%', #{name}, '%') or s.student_no like concat('%', #{name}, '%') or p.practice_name like concat('%', #{name}, '%'))
and p.practice_type = #{practiceType}
and sp.status = #{status}
order by sp.created_time desc limit #{limit} offset #{offset}
